mapper.insert:
INSERT INTO comment (
	content, member_no, board_no, regdate, tablename
)
VALUES ( 
	#{content}, #{member_no}, #{board_no}, NOW(), #{tablename}
)

mapper.select:


SELECT count(*) 
FROM comment
WHERE board_no = #{board_no} AND tablename = #{tablename}

SELECT * FROM comment
WHERE board_no = #{board_no} AND tablename = #{tablename}
ORDER BY regdate DESC
LIMIT ${startIdx}, ${rowPerPage}
