mapper.select:
SELECT *, (SELECT count(*) FROM report sub WHERE sub.board_no = report.board_no AND sub.reply_no = report.reply_no AND sub.stat != 'reject') sumCnt FROM report
WHERE stat = #{stat}
ORDER BY report_no DESC

SELECT *,
	(SELECT count(*) 
	FROM report sub 
	WHERE sub.board_no = report.board_no 
		AND sub.reply_no = report.reply_no 
		AND sub.stat != 'reject') sumCnt
FROM report 
WHERE you_no = #{you_no} 

	AND stat = #{stat}

ORDER BY report_no DESC;

SELECT count(*) sumCnt
FROM report sub 
WHERE you_no = #{you_no}  

	AND stat = #{stat}
